import {Form, Button } from "react-bootstrap";
import ConfirmSignin from "./ConfirmSignin";

export default class  Login extends React.Component {

    state = {
        username: '',
        password: ''
    };

    handleLogin() {
        console.log(this.state.username);
    }

    render() {
        return(
            <div className="row d-flex justify-content-center">
                <div className="col-md-3 m-5">
                    <Form>
                        <Form.Group controlId="formBasicPhoneNumber">
                            <Form.Label>Enter your phone number</Form.Label>
                            <Form.Control type="phoneNumber" placeholder="+383999999" onChange={username => this.setState({ username: username.target.value})}/>
                            <Form.Text className="text-muted">
                                We'll never share your email with anyone else.
                            </Form.Text>
                        </Form.Group>
                        <Button variant="secondary" type="submit" onClick={() => this.handleLogin()}>
                            Get Verification Code
                        </Button>
                    </Form>

                    <ConfirmSignin number={contact.phoneNumber} />
                </div>

            </div>
        )
    }
}
