import React from "react";
import {Form, Button} from "react-bootstrap";
import ConfirmSignin from "./ConfirmSignin";

export default class Login extends React.Component {

    state = {
        username: '',
        password: ''
    };

    handleLogin() {
        console.log(this.state.username);
    }

    render() {
        return (
            <div className="row d-flex justify-content-center">
                <div className="col-md-3 m-5">
                    <Form>
                        <Form.Group controlId="formBasicPhoneNumber">
                            <Form.Control
                                className="m-2"
                                type="phoneNumber"
                                placeholder="Enter email or username"
                                onChange={u => this.setState({username: u.target.value})}/>
                            <Form.Control
                                className="m-2"
                                type="password"
                                placeholder="Password"
                                onChange={p => this.setState({password: p.target.value})}/>
                            <Form.Text className="text-muted m-2">
                                We'll never share your email with anyone else.
                            </Form.Text>
                        </Form.Group>
                        <Button className="text-success m-2" variant="secondary" type="submit" onClick={() => this.handleLogin()}>
                            Login
                        </Button>
                    </Form>

                    <ConfirmSignin number={this.state.username}/>
                </div>

            </div>
        )
    }
}
