import React from "react";
import {Form, Button } from "react-bootstrap";
import ConfirmSignIn from "./ConfirmSignIn";

const Login = () => {



    return(
        <div className="row d-flex justify-content-center">
            <div className="col-md-3 m-5">
                <Form>
                    <Form.Group controlId="formBasicPhoneNumber">
                        <Form.Label>Enter your phone number</Form.Label>
                        <Form.Control type="phoneNumber" placeholder={contact.phoneNumber} onChange={n => setContact({...contact, phoneNumber: n.target.value } )}/>
                        <Form.Text className="text-muted">
                            We'll never share your email with anyone else.
                        </Form.Text>
                    </Form.Group>
                    <Button variant="secondary" type="submit" onClick={() => handleLogin()}>
                        Get Verification Code
                    </Button>
                </Form>

                <ConfirmSignIn number={contact.phoneNumber} />
            </div>

        </div>
    )
};

export default Login;
